<script>
  import Vue from 'vue'
  import domtoimage from 'dom-to-image';
  import Rater from '../../components/rater/index.vue'
  import TransferDom from '../../directives/transfer-dom/index'
  import VueScroller from '../../components/vue-scroller/index'
  Vue.use(VueScroller)
  export default {
    components:{
      Rater
    },
    directives: {
      TransferDom
    },
    data(){
      return{
        raterSize:35,               //任务评级的字体大小（ios和Android的字体不一样）
        taskLevel:4.5,              //任务评分
        shareDirection:0,           //分享方向，0:微信，1：朋友圈，2：QQ :3：微博
        selectedValue:0,            //0为选择百分比，1为排名
        workLevel:2,                //目前分为4个等级，0为最高等级
        themeColor:'',              //主体颜色，
        shareItem:'',               //选择百分比和排名的项中的背景图片
        shareType:0,                //0为选择百分比和排名，1为分享到微信页
      }
    },
    created(){
      this.workLevel = 3;
      this.setTheme();
    },
    mounted(){
      //设置任务评级星级的字体大小
      let isIphone = window.navigator.appVersion.match(/iphone|ipad|ipod/gi);
      if(isIphone){
        this.raterSize = 35;
      }else{
        this.raterSize = 11;
      }
    },
    methods:{
      setTheme(){
        if(this.workLevel === 0){
          this.themeColor = '#E8C57B';
          this.shareItem = 'kpi-per-item-0';
        }else if(this.workLevel === 1){
          this.themeColor = '#549CEB';
          this.shareItem = 'per-item-1';
        }else if(this.workLevel === 2){
          this.themeColor = '#30E6BD';
          this.shareItem = 'per-item-2';
        }else if(this.workLevel === 3){
          this.themeColor = '#DDFFF7';
          this.shareItem = 'per-item-3';
        }
      },
      shareGrade(){
        this.shareType = 1;
      },
      onCancel(){
        this.shareType = 0;
      },
      switchImage(index){
        let self = this;
        // 获取dom结构
        this.shareDirection = index;
        // 显示
        this.$vux.loading.show({
          text: ''
        })
        let card_target = document.getElementById("content");
        domtoimage.toPng(card_target).then(function(dataUrl) {
          //dataUrl 为base64格式
          //上传图片
          self.fileUploadChange(dataUrl)
        })
      },
      fileUploadChange (files) {
        console.log(files)
        const self = this
        let formdata = new FormData();
        let type;
        try {
          type= files.match(/:(.*?);/)[1].split('/')[1];
        }catch (e) {
          // 隐藏
          self.$vux.loading.hide();
          self.showToast('分享失败','auto');
        }

        formdata.append('file[]', self.dataURLtoBlob(files), Math.random()+'.'+type);
        var xhr = new XMLHttpRequest();

        xhr.open('POST', 'https://www.erplus.co/upload/uploadAttachment');

        xhr.onload = function () {
          if (xhr.status === 200) {
            // 上传成功
            console.log('上传成功');
            var res = JSON.parse(xhr.response);
            if (res.respCode === '000') {
              res.item.forEach(function (obj,index) {
                var uploadImgSrc = ''
                if(res.item[0].indexOf('/') === -1){
                  var uploadImgArr = res.item[index].split('.')
                  var uploadImgFrontArr = uploadImgArr[0].split('-')
                  uploadImgFrontArr.forEach(function (src) {
                    uploadImgSrc += '/' + self.hexToDecimal(src)
                  })
                  uploadImgSrc += '.' + uploadImgArr[1];
                }else{
                  uploadImgSrc = obj
                }
                let imageUrl = 'https://www.erplus.co/image' + uploadImgSrc;
                self.goToApp('SHARE_ATTENDANCE_INFO',self.shareDirection, imageUrl);
                // 隐藏
                self.$vux.loading.hide()
              })
            }
          } else {
            // 处理其他情况
          }
        }

        xhr.onerror = function () {
          // 处理错误
        };

        xhr.upload.onprogress = function (e) {
          // 上传进度
//            var percentComplete = ((e.loaded / e.total) || 0) * 100;
//          self.uploadCount = parseInt(((e.loaded / e.total) || 0) * 100) + '%'
        }
        // 触发上传
        xhr.send(formdata);
        /* ==================================================== */

//        event.target.value = '' /*清空文件值*/
      },
      //**dataURL to blob**
      dataURLtoBlob(dataurl) {
        var arr = dataurl.split(','), mime = arr[0].match(/:(.*?);/)[1],
          bstr = atob(arr[1]), n = bstr.length, u8arr = new Uint8Array(n);
        while (n--) {
          u8arr[n] = bstr.charCodeAt(n);
        }
        return new Blob([u8arr], { type: mime });
      },
      switchSelected(value){
        this.selectedValue = value
      },
      goToApp(target, direction, imageUrl){
        console.log({
          "target":target,
          "share":direction,//0:微信，1：朋友圈，2：QQ :3：微博
          "imageUrl":imageUrl
        })
        this.postMsgToApp({
          "target":target,
          "share":direction,//0:微信，1：朋友圈，2：QQ :3：微博
          "imageUrl":imageUrl
        })
      }
    }
  }
